mapper.select:
SELECT media_id, type_id, haibao, fengmian, biaoti FROM media_info WHERE 1 = 1 AND type_id = #{typeId} AND status = #{status}
AND (biaoti LIKE CONCAT('%',#{keyWord},'%') OR bieming LIKE CONCAT('%',#{keyWord},'%'))
ORDER BY update_time DESC

mapper.delete:
DELETE
FROM
media_info
WHERE
user_id = #{userId}

    AND media_id = #{mediaId}
