mapper.update:
UPDATE video_info
media_id=#{mediaId}, title=#{title}, image=#{image}, url=#{url}, player_id=#{playerId}, status=#{status}, remark=#{remark}, update_time=#{updateTime}, view_count=#{viewCount},
WHERE video_id = #{videoId}

UPDATE video_info
SET image = #{image}
WHERE
	media_id = #{mediaId}

UPDATE video_info
SET power = #{power}
WHERE
	media_id = #{mediaId}

UPDATE video_info
SET status = #{status}
WHERE video_id IN

		#{item}
